// NutritionalGoals.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link

const NutritionalGoals = ({ onSubmit }) => {
  const [protein, setProtein] = useState(0);
  const [carbs, setCarbs] = useState(0);
  const [fats, setFats] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ protein, carbs, fats });
  };

  const [recipes, setRecipes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/recipes", {
          params: { page: currentPage, limit: 10 },
        });
        setRecipes(response.data.recipes);
        setTotalPages(response.data.totalPages);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, [currentPage]);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Protein (g):
        <input type="number" value={protein} onChange={(e) => setProtein(e.target.value)} />
      </label>
      <label>
        Carbs (g):
        <input type="number" value={carbs} onChange={(e) => setCarbs(e.target.value)} />
      </label>
      <label>
        Fats (g):
        <input type="number" value={fats} onChange={(e) => setFats(e.target.value)} />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default NutritionalGoals;
