import React, { useState, useEffect } from "react";
import axios from "axios";

function RecipeList() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  const addToMealPlan = (recipeId) => {
    // Logic to add recipe to the meal plan
    console.log(`Adding recipe ${recipeId} to meal plan.`);
  };

  return (
    <div>
      <h1>Recipes</h1>
      {recipes.map((recipe) => (
        <div key={recipe.RecipeId} style={{ display: "flex", alignItems: "center", margin: "10px 0" }}>
          <img
            src={recipe.Images[0] || "placeholder-image-url.jpg"} // Use the first image or a placeholder
            alt={recipe.Name}
            style={{ width: "100px", height: "100px", objectFit: "cover", marginRight: "10px" }}
          />
          <div>
            <h3>{recipe.Name}</h3>
            <button onClick={() => addToMealPlan(recipe.RecipeId)}>Add to Meal Plan</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default RecipeList;
