import React, { useState, useContext } from "react";
import Calendar from "react-calendar";
import { MealPlanContext } from "/Users/uduthurishruthika/Desktop/SE03/meal-planning/meal-planning-frontend/src/components/MealPlanContext.js";
import "./MealPlanner.css";

const MealPlanner = () => {
  const { mealPlan, addRecipeToMealPlan } = useContext(MealPlanContext);
  const [date, setDate] = useState(new Date());

  const handleDrop = (event) => {
    const recipe = JSON.parse(event.dataTransfer.getData("recipe"));
    const selectedDate = date.toDateString();
    addRecipeToMealPlan(selectedDate, recipe);
  };

  const handleDragStart = (event, recipe) => {
    event.dataTransfer.setData("recipe", JSON.stringify(recipe));
  };

  return (
    <div>
      <h1>Meal Planner</h1>
      <Calendar onChange={setDate} value={date} />
      <div onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
        <h2>Recipes for {date.toDateString()}</h2>
        <ul>
          {(mealPlan[date.toDateString()] || []).map((recipe, index) => (
            <li key={index} draggable onDragStart={(e) => handleDragStart(e, recipe)}>
              {recipe.Name}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MealPlanner;
