import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import useNavigate

function Login({ onLogin }) { // Accept onLogin as prop
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Initialize useNavigate

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });
      const token = response.data.token;
      localStorage.setItem("auth-token", token); // Store token in localStorage
      alert("Login successful");
      
      onLogin(); // Call onLogin to update authentication state
      navigate("/"); // Redirect to main page after successful login
    } catch (error) {
      console.error("Error logging in:", error);
      alert("Invalid username or password."); // Optional: Alert on failure
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input
        type="text"
        placeholder="Username"
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Login</button>
    </form>
  );
}

export default Login;
