import React from 'react';
import './LandingPage.css';
import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const LandingPage = () => {
    const [mousePosition, setMousePosition] = useState({
        x: 0,
        y: 0
      });
      const [cursorVariant, setCursorVariant] = useState("default");


      useEffect(() => {
        const mouseMove = e => {
          setMousePosition({
            x: e.clientX,
            y: e.clientY
          })
        }

        window.addEventListener("mousemove", mouseMove);

        return () => {
          window.removeEventListener("mousemove", mouseMove);
        }
      }, []);

      const variants = {
        default: {
          x: mousePosition.x - 16,
          y: mousePosition.y - 16,
        },
        text: {
          height: 100,
          width: 100,
          x: mousePosition.x - 75,
          y: mousePosition.y - 75,
          backgroundColor: "#f73302",
          mixBlendMode: "screen"
        }
      }

      const textEnter = () => setCursorVariant("text");
      const textLeave = () => setCursorVariant("default");
    return (
        <div className='main-container'>
            <nav className='main-nav'>
                <div className='left-nav'>
                    <p className='nav-item'>Blog</p>
                    <p className='nav-item'>Meal Plans</p>
                    <p className='nav-item'>Recipes</p>
                </div>
                <img src = "https://res.cloudinary.com/dujmpn87j/image/upload/v1730144316/Bright_Colorful_Playful_Funny_Donuts_Food_Circle_Logo_processed_yb2a5y.png" alt="Meal Planning App Logo" className='logo-image'/>
                <div className='right-nav'>
                    <p className='nav-item'>About Us</p>
                    <p className='nav-item'>Contact Us</p>
                    <p className='nav-item'>Get Started</p>
                </div>
            </nav>
            <div className='section-one'>
                <div className="heading">
                    <motion.div
                        className='cursor'
                        variants={variants}
                        animate={cursorVariant}
                    />
                    <h1 onMouseEnter={textEnter} onMouseLeave={textLeave} className='title'>Welcome to SavvyEats!!!</h1>
                    <p className='content-text'>Savvy Eats is a meal planning app that helps you plan your meals and achieve your nutritional goals. Our platform provides personalized meal plans, recipes, and nutritional information to help you make informed decisions about your diet. Whether you're looking to lose weight, gain muscle, or simply eat healthier, Savvy Eats has you covered. Sign up today to get started on your journey to better health!</p>
                </div>
                <div>
                    <img src = "https://res.cloudinary.com/dujmpn87j/image/upload/v1730150780/Yellow_Illustrated_Meal_Planner_djtgrr.png" alt = "Healthy Food" className='food-image'/>
                </div>
            </div>
            <div className='content'>
                <div className='content-left'>
                    <h2 className='content-heading'>About Us</h2>
                    <p className='content-text'>Savvy Eats is a meal planning app that helps you plan your meals and achieve your nutritional goals. Our platform provides personalized meal plans, recipes, and nutritional information to help you make informed decisions about your diet. Whether you're looking to lose weight, gain muscle, or simply eat healthier, Savvy Eats has you covered. Sign up today to get started on your journey to better health!</p>
                </div>
                <div className='content-right'>
                    <h2 className='content-heading'>Get Started</h2>
                    <p className='content-text'>Ready to take control of your diet and start eating healthier? Sign up for a free account today to access personalized meal plans, recipes, and nutritional information. With Savvy Eats, you'll have everything you need to achieve your nutritional goals and live a healthier lifestyle. Get started today and start making better food choices!</p>
                </div>
            </div>
        </div>
    );
};

export default LandingPage;
