import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import NutritionalGoals from './components/NutritionalGoals';
import Login from './components/Login'; // Import your Login component
import Register from './components/Register'; // Import your Register component
import { useState } from 'react';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Track authentication status

  const handleGoalsSubmit = async (goals) => {
      try {
          const response = await fetch('http://localhost:5000/api/goals', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify(goals),
          });

          if (!response.ok) {
              throw new Error('Network response was not ok');
          }

          const data = await response.json();
          console.log(data); // Handle the filtered recipes here
      } catch (error) {
          console.error('Error:', error);
      }
  };

  return (
      <Router>
          <div>
              <h1>Meal Planning</h1>
              <Routes>
                  <Route path="/" element={isAuthenticated ? <NutritionalGoals onSubmit={handleGoalsSubmit} /> : <Navigate to="/login" />} />
                  <Route path="/components/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />
                  <Route path="/components/register" element={<Register />} />
              </Routes>
          </div>
      </Router>
  );
};

export default App;
