// RecipeDetail.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function RecipeDetail() {
  const { id } = useParams(); // Get the recipe ID from the URL
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/recipes/${id}`); // Adjust the API endpoint as needed
        setRecipe(response.data);
      } catch (error) {
        console.error("Error fetching recipe:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  if (loading) return <p>Loading...</p>;

  if (!recipe) return <p>Recipe not found.</p>;

  return (
    <div>
      <h1>{recipe.Name}</h1>
      <img src={recipe.Images[0]} alt={recipe.Name} style={{ width: "300px", height: "300px" }} />
      <p><strong>Cook Time:</strong> {recipe.CookTime}</p>
      <p><strong>Prep Time:</strong> {recipe.PrepTime}</p>
      <p><strong>Total Time:</strong> {recipe.TotalTime}</p>
      <p><strong>Description:</strong> {recipe.Description}</p>
      <p><strong>Recipe Category:</strong> {recipe.RecipeCategory}</p>
      <p><strong>Keywords:</strong> {recipe.Keywords.join(", ")}</p>
      <p><strong>Calories:</strong> {recipe.Calories}</p>
      <p><strong>Ingredients:</strong></p>
      <ul>
        {recipe.RecipeIngredientQuantities.map((quantity, index) => (
          <li key={index}>{quantity} {recipe.RecipeIngredientParts[index]}</li>
        ))}
      </ul>
      <p><strong>Instructions:</strong></p>
      <ol>
        {recipe.RecipeInstructions.map((instruction, index) => (
          <li key={index}>{instruction}</li>
        ))}
      </ol>
    </div>
  );
}

export default RecipeDetail;
